  return (
    <div className='flex flex-col justify-center items-center mt-32'>   
        <h1 className='font-semibold text-[25px]'>Populer Products From All Brands</h1>
        <div className='flex justify-center items-center mt-44 bg-[#8a8a8a]'>
            <Swiper
            slidesPerView={3}
            spaceBetween={30}
            centeredSlides={true}
            autoplay={{
              delay: 3000,
              disableOnInteraction: false,
            }}
            pagination={{
              clickable: true,
            }}
            navigation={true}
            modules={[Autoplay, Navigation, Pagination]}
            className='w-screen'
            >

        <SwiperSlide className="flex items-center justify-center flex-col">
