import { createSlice } from "@reduxjs/toolkit";


const sliceCart =  createSlice({
    name: 'carts',
    initialState: {
        cart: []
    },
    reducers: {
        getCartItems: (state, action) => {
            state.cart = action.payload
        }
    }

})


export const { getCartItems } = sliceCart.actions
export default sliceCart.reducer
