import React, { useEffect, useRef, useState } from "react";

  // import Swiper JS
  import { Swiper, SwiperSlide } from "swiper/react";
  import { Pagination, Autoplay, Navigation } from "swiper";

  // import Swiper styles
  import 'swiper/css';
  import 'swiper/css/pagination';
  import 'swiper/css/navigation';
  

import fetchAllProducts from '../../utilities/fetchAllProducts'

import TopProductsSingle from "./TopProductsSingle";

function TopProducts() {

  const [product, setProduct] = useState([])
  
  
  useEffect(() => {
    fetchAllProducts().then(data => setProduct(data.products))
  }, [])

  const sliced = product.slice(1, 11)
console.log(sliced)
  return (
    <div className='flex flex-col justify-center items-center mt-32'>   
        <h1 className='font-semibold text-[25px]'>Populer Products From All Brands</h1>
        <div className='flex justify-center items-center mt- '>
            <Swiper
            slidesPerView={3}

            centeredSlides={true}
            autoplay={{
              delay: 3000,
              disableOnInteraction: false,
            }}
            pagination={{
              clickable: true,
            }}
            navigation={true}
            modules={[Autoplay, Navigation, Pagination]}
            className='w-100vh'
            >

            {sliced?.map(
              (el, index) => {
                return(
                  <SwiperSlide>
                  <TopProductsSingle data={el}/>
                  </SwiperSlide>
                )
              }
            )}

        
          </Swiper>

        </div>
    </div>  
  )
}

export default TopProducts
