import React from 'react'
import { useLoaderData, useParams } from 'react-router-dom'

function SingleProduct() {
    
    const { id } = useParams()

    const loader = useLoaderData()

  return (
    <div>
      <div className='bg-[#fff] flex items-center justify-center drop-shadow-[rgba(0,0,0,0.6)] p-2 flex-row'>
        <div className='flex flex-col justify-center items-center'>
          <p>In stock: {loader.stock}</p>
          <img className='rounded-3xl object-contain h-56 w-96' src={loader.thumbnail} alt="sslikica" />
        </div>
        
        <p>{loader.title}</p>
        <p>{loader.brand}</p>
        <p>{loader.description}</p>
        <p>${loader.price}</p>
      </div>
    </div>
  )
}


export const singleProductLoader = async( {params} ) => {
    const { id } = params
    let data = await fetch(`https://dummyjson.com/products/${id}`)
    return data.json()
}

export default SingleProduct
